    }
}

@include up(992) {
    .projects {
        .projects__list {
            height: 75%;
            max-height: 500px;
            width: 100%;
        }

        .projects__link {
            line-height: calc(500px / 4);
            overflow: hidden;
            position: relative;

            .project__img {
                transition: all 0.3s ease-in-out;
                opacity: 1;
            }

            .projects__item:hover .project__img,
            .projects__item .project__img.clicked {
                clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
            }
        }
    }
}

sources.scss/pages/_project.scss:
.project__title {
    height: 50vh;
    height: calc(var(--vh, 1vh) * 50);
    display: flex;
    align-items: center;

    h1 {
        overflow: hidden;

        span {
            display: inline-block;
        }
    }
}

.project__link {
    a {
        text-transform: uppercase;
        font-weight: bold;
        padding-top: 10px;
        padding-bottom: 10px;
        font-size: 0.875rem;
    }
}

.project__infos {
    margin: 40px auto;

    div {
        margin-bottom: 40px;
        padding-right: 50px;

        span {
            text-transform: uppercase;
            font-size: 0.875rem;
            font-weight: bold;
            opacity: 0.75;
        }

        p {
            font-size: 1.125rem;
        }
    }
}

@include up(768) {
    .project__infos {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
    }

    .project__year {
        width: 25%;
    }
    .project__role {
        width: 50%;
    }

    .project__description {
        width: 75%;
    }
}

@include up(1200) {
    .project__year {
        width: 12.5%;
    }
    .project__role {
        width: 25%;
    }

    .project__description {
        width: 50%;
    }
}

.project__images {
    img {
        display: block;
        margin-top: 25px;
        transform: scale(0.9);
        transition: transform 0.3s ease-in-out, opacity 0.3s ease-in-out;
    }

    img.img-big {
        transform: scale(0.96);
    }

    img.active {
        transform: translateY(0) scale(1);
    }
}

@include up(768) {
    .project__images {
        padding-left: 50px;
        padding-right: 50px;
        padding-bottom: 50px;

        img {
            margin-top: 50px;
        }
    }
}

@include up(992) {
    .project__text {
        position: fixed;
        top: 0;
        height: 95vh;
        height: calc(var(--vh, 1vh) * 95);

        .project__title.asleep,
        .project__link.asleep,
        .project__infos.asleep {
            opacity: 0;
        }
    }

    .project__images {
        margin-top: 95vh;
        padding-left: 162px;
        padding-right: 162px;
        // background-color: var(--black);
        position: relative;
        z-index: 2;
        padding-bottom: 162px;

        img:first-child {
            transform: translateY(0);
        }

        img {
            margin-top: 100px;
        }
    }
}

.project__footer {
    display: flex;
    justify-content: space-between;
    background-color: var(--white);
    color: var(--black);
    height: 190px;
    padding-top: 30px;

    a:last-child {
        text-align: right;
        max-width: 40%;
    }

    span {
        display: inline-block;
        text-transform: uppercase;
        font-weight: bold;
        font-size: 0.625rem;
        position: relative;
        transition: transform 0.2s ease-in-out;
    }

    p {
        font-size: 1.5rem;
        font-family: $font-alt;
        line-height: 1;
    }
}

@include up(768) {
    .project__footer {
        span {
            font-size: 0.825rem;
        }

        p {
            font-size: 2rem;
        }
    }
}

@include up(992) {
    .project__footer {
        align-items: center;
        height: 230px;
        padding-top: 0;
        position: relative;
        z-index: 2;

        .footer__prev,
        .footer__next {
            span {
                transition: transform 0.2s ease-in-out;
            }

            span::after {
                font-size: 1.125rem;
                position: absolute;
                bottom: -3px;
                opacity: 0;
                transition: opacity 0.2s ease-in-out;
            }

            &:hover {
                span::after {
                    opacity: 1;
                    transition: opacity 0.2s ease-in-out;
                }
            }
        }

        .footer__prev {
            span::after {
                content: "<";
                left: -20px;
            }

            &:hover {
                span {
                    transform: translateX(20px);
                }
            }
        }

        .footer__next {
            span::after {
                content: ">";
                right: -20px;
            }

            &:hover {
                span {
                    transform: translateX(-20px);
                }
            }
        }

        p {
            font-size: 2.5rem;
            line-height: 150%;
        }
    }
}

sources.scss/pages/_about.scss:
.about {
    position: relative;
    z-index: 20;
    width: 100%;
    background-color: var(--black);

    .top__link {
        z-index: 40;
    }
}

.about__infos {
    font-size: 1.125rem;
    width: 100%;

    .about__photo {
        width: 100%;
        height: 60vh;
        height: calc(var(--vh, 1vh) * 60);
        overflow: hidden;
        background-image: linear-gradient(0deg, var(--black) 0%, transparent 25%), url("../img/profil.jpg");
        background-size: cover;
        background-position: 50% 50%;
    }

    .about__perso {
        position: relative;
        top: -10vh;
        top: calc(var(--vh, 1vh) * -10);
        margin-bottom: -10vh;
        margin-bottom: calc(var(--vh, 1vh) * -10);

        > p {
            margin-bottom: 40px;
        }

        > div {
            margin-bottom: 20px;

            span {
                font-size: 0.875rem;
                text-transform: uppercase;
                font-weight: bold;
                opacity: 0.75;
            }
        }

        .about__scroll {
            margin-bottom: 0;
            height: 0;
        }
    }
}

.about__description {
    text-transform: uppercase;
    background-color: var(--black);
    position: relative;
    z-index: 30;
    margin-top: 70px;
    margin-bottom: 40px;
    background-color: var(--black);

    p {
        margin-bottom: 20px;
        font-size: 1.5rem;
    }
}

@include up(768) {
    .about__infos {
        position: fixed;
        top: 0;
        left: 0;
        height: 100vh;
        height: calc(var(--vh, 1vh) * 100);
        display: flex;
        align-items: flex-end;
        background-color: var(--black);

        .about__photo {
            width: 50%;
            height: 100%;
            background-image: linear-gradient(-90deg, var(--black) 0%, transparent 25%), url("../img/profil.jpg");
        }

        .about__perso {
            top: initial;
            margin-bottom: 0;

            .about__scroll {
                margin-bottom: 0;
                margin-top: 100px;
                height: 100px;
                width: 1px;
                background-color: var(--white);
                position: relative;
                animation: scroll 1s ease-out infinite;
            }
        }
    }

    .about__description {
        min-height: 100vh;
        margin-top: 100vh;
        margin-top: calc(var(--vh, 1vh) * 100);
        margin-bottom: 0;
        padding-top: 150px;
        padding-bottom: 150px;

        :last-child {
            margin-bottom: 0;
        }
    }
}

@include up(768) {
    .about__description {
        padding-right: 200px !important;

        p {
            margin-bottom: 50px;
            font-size: 2.25rem;
        }
    }
}

@keyframes scroll {
    0% {
        transform: translateY(100%);
        opacity: 0.5;
    }

    100% {
        transform: translateY(0);
        opacity: 0;
    }
}
